package condicao;

public class exercicioCondicao2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float number = 2.99f;
		
		if(number < 1.99){
			System.out.println("Valor a baixo de 1.99");
		}else if(number > 5.99){
			System.out.println("Valor maior de 5.99");
		}else{
			System.out.println("Valor entre 1.99 e 5.99");
		}
	}

}
